import React from 'react'
import axios from 'axios'
import {useState,useEffect} from 'react'
import { FaEdit } from 'react-icons/fa';
import { MdDelete } from 'react-icons/md';

const Index = () => {
    const [posts,setPosts]=useState([]);
    const apiEndPoint='https://jsonplaceholder.typicode.com/posts';
    useEffect(()=>{
        const getPosts= async ()=>{
            const response= await axios.get(apiEndPoint)
            setPosts(response.data);        }
        getPosts();
    },[])
    useEffect(()=>{

    },[])
  return (
    <div className="container">
         <div className='mt-3'>
        <h2 className='text-center'>There are {posts.length} posts</h2>

        <table className='table table-striped'>
            <thead>
                <tr>
                    <th>Post Id</th>
                    <th>Content</th>
                    <th>Update</th>
                    <th>Delete</th>
                </tr>
            </thead>
            <tbody>
                {
                    posts && posts.map(post=>{
                        return(
                            <tr>
                                <td>{post.id}</td>
                                <td>{post.body.length>100 ? post.body.slice(0,120) + "..." : post.body}</td>
                                <td>
                                    <button className='btn btn-warning btn-sm'><FaEdit/> Edit</button>
                                </td>
                                <td>
                                    <button className='btn btn-danger btn-sm'><MdDelete/> Delete</button>
                                </td>
                            </tr>
                        )
                    })
                }
            </tbody>
        </table>
    </div>
    </div>
  )
}

export default Index
