  class Card extends Component {
    constructor(){
        super();

        this.state={
            isClose:false,
            text:false
        }
    }

    ToggleHandler=(e)=>{
        this.setState({
            text:!this.state.text,
            isClose:!this.state.isClose  
        })
    }
  render() {
    return (
      <div>
        <div className="card col-lg-4 mt-4">
           <div className='card-header text-end'>
             <span onClick={this.ToggleHandler}>{this.state.text ? "Open+" : "Close- "}</span>
           </div>
            {

            }
        </div>
      </div>
    )
  }  
}

export default Card
