import React from 'react'
import axios from 'axios'
import {useState,useEffect} from 'react'
import { FaEdit } from 'react-icons/fa';
import { MdDelete } from 'react-icons/md';
import PulseLoader from "react-spinners/PulseLoader";

const Index = () => {
    const [posts,setPosts]=useState([]);
    const[loader,setLoader]=useState(true); 
    const apiEndPoint='https://jsonplaceholder.typicode.com/posts';
    useEffect(()=>{
        const getPosts= async ()=>{
            const response= await axios.get(apiEndPoint)
            setPosts(response.data);}
        getPosts();
    },[])
    useEffect(()=>{

    },[])


    const AddPost= async ()=>{
        const newPost={
            title: "GROUP P128",
            body:"new programming group"
        } 
        await axios.post(apiEndPoint,newPost)
        setPosts([newPost,...posts])
    } 

    const DeletePost=async(post)=>{
        await axios.delete(`${apiEndPoint}/${posts.id}`)
        setPosts(posts.filter(x=>x.id !== post.id))
    }

  return (
    <div className="container">
         <div className='mt-3'>
        <h2 className='text-center'>There are {posts.length} posts</h2>
        <button onClick={AddPost} className='btn btn-primary w-100 mb-3'>ADD POST</button>
        <table className='table table-striped table-bordered'>
            <thead>
                <tr>
                    <th>Post Id</th>
                    <th>Content</th>
                    <th>Update</th>
                    <th>Delete</th>
                </tr>
            </thead>
            <tbody>
                {
                    posts && posts.map(post=>{
                        return(
                            <tr key={post.id}>
                                <td>{post.id}</td>
                                <td>{post.body.length>100 ? post.body.slice(0,120) + "..." : post.body}</td>
                                <td>
                                    <button className='btn btn-warning btn-sm'><FaEdit/> Edit</button>
                                </td>
                                <td>
                                    <button onClick={()=>{DeletePost(post)}} className='btn btn-danger btn-sm'><MdDelete/> Delete</button>
                                </td>
                            </tr>
                        ) 
                    })
                }
            </tbody>
        </table>
    </div>
    </div>
  )
}

export default Index
